Vec& Vec::operator=(const Vec& original) {
    if (this != &original) {
        if (mySize != original.mySize) {
            if (mySize &gt; 0) {
                delete[] myArray;
                myArray = nullptr;
            }
            if (original.mySize &gt; 0) {
                myArray = new Item[original.mySize];
            }
            mySize = original.mySize;
        }
        for (int i = 0; i &lt; mySize; ++i) {
            myArray[i] = original.myArray[i];
        }
    }
    return *this;
}

